import sysE,S,M = map(int,sys.stdin.readline().split())"""(1 ≤ E ≤ 15, 1 ≤ S ≤ 28, 1 ≤ M ≤ 19)"""E1 = 0S1 = 0M1 = 0result = 0while True:    if E1 == E and S1 == S and M1 == M:        break    else:        result += 1        E1 += 1        S1 += 1        M1 += 1        if E1 // 16 == 1:            E1 = 1        if S1 // 29 == 1:            S1 = 1        if M1 // 20 == 1:            M1 = 1print(result)